mapper.select:
SELECT h.ID,TITLE,price,floorage,contact,path,s.name AS sname,d.name AS dname,t.name AS tname,
ispass,s.id AS sid,d.ID AS did,t.ID AS tid FROM house AS h
INNER JOIN street AS s ON h.STREET_ID=s.ID
INNER JOIN district AS d ON d.ID=s.DISTRICT_ID
INNER JOIN TYPE AS t ON t.ID=h.TYPE_ID
WHERE 1=1 and ispass=1

and title like #{title}


and price > #{startPrice}


and price < #{endPrice}


and d.id = #{did}


and s.id = #{sid}


and t.id = #{tid}


and floorage between #{startFlooa} and #{endFlooa}
